import React, { useState } from 'react';
import CreateRequirementModal from './CreateRequirementModal';
import RequirementDetailsModal from './RequirementDetailsModal';

const RequirementsTab = ({ projectId, Requirements, onRefresh }) => {
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedRequirement, setSelectedRequirement] = useState(null);

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h3 className="text-lg font-semibold">Project Requirements</h3>
        <button
          onClick={() => setShowCreateModal(true)}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          Create Requirement
        </button>
      </div>

      <div className="space-y-4">
        {Requirements.map((Requirement) => (
          <div
            key={Requirement.id}
            onClick={() => setSelectedRequirement(Requirement)}
            className="p-4 border border-gray-200 rounded-lg hover:border-blue-500 cursor-pointer"
          >
            <div className="flex justify-between items-start mb-2">
              <h4 className="font-semibold text-gray-900">{Requirement.title}</h4>
              <span className={`px-2 py-1 text-xs rounded ${
                Requirement.priority === 'HIGH' ? 'bg-red-100 text-red-800' :
                Requirement.priority === 'MEDIUM' ? 'bg-yellow-100 text-yellow-800' :
                'bg-green-100 text-green-800'
              }`}>
                {Requirement.priority}
              </span>
            </div>
            <p className="text-gray-600 text-sm mb-2">{Requirement.description}</p>
            <div className="flex items-center gap-4 text-xs text-gray-500">
              <span className={`px-2 py-1 rounded ${
                Requirement.status === 'PENDING' ? 'bg-gray-100' :
                Requirement.status === 'IN_PROGRESS' ? 'bg-blue-100 text-blue-800' :
                'bg-green-100 text-green-800'
              }`}>
                {Requirement.status}
              </span>
              {Requirement.assignee && (
                <span>Assigned to: {Requirement.assignee.fullName}</span>
              )}
            </div>
          </div>
        ))}
      </div>

      {Requirements.length === 0 && (
        <div className="text-center text-gray-500 py-12">
          No Requirements yet. Create one to get started!
        </div>
      )}

      {showCreateModal && (
        <CreateRequirementModal
          projectId={projectId}
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            onRefresh();
          }}
        />
      )}

      {selectedRequirement && (
        <RequirementDetailsModal
          Requirement={selectedRequirement}
          onClose={() => setSelectedRequirement(null)}
          onRefresh={onRefresh}
        />
      )}
    </div>
  );
};

export default RequirementsTab;
