import React, { useState } from 'react';
import RequirementCard from './RequirementCard';
import CreateRequirementModal from './CreateRequirementModal';
import RequirementDetailsModal from './RequirementDetailsModal';
import { useAuth } from '../context/AuthContext';

const RequirementsTab = ({ projectId, requirements = [], onRefresh, project }) => {
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedRequirement, setSelectedRequirement] = useState(null);
  const { user } = useAuth();

  // Check if current user is the owner
  const isOwner = user && project && project.owner && user.id === project.owner.id;

  // Safely filter requirements by status
  const safeRequirements = Array.isArray(requirements) ? requirements : [];
  const pendingRequirements = safeRequirements.filter(r => r.status === 'PENDING');
  const inProgressRequirements = safeRequirements.filter(r => r.status === 'IN_PROGRESS');
  const completedRequirements = safeRequirements.filter(r => r.status === 'DONE');

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h3 className="text-lg font-semibold">Project Requirements</h3>
          <p className="text-sm text-gray-600 mt-1">
            {isOwner 
              ? 'Post requirements for different tech stack developers' 
              : 'View and apply for project requirements'}
          </p>
        </div>
        {isOwner && (
          <button
            onClick={() => setShowCreateModal(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Post Requirement
          </button>
        )}
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
          <div className="text-2xl font-bold text-gray-900">{pendingRequirements.length}</div>
          <div className="text-sm text-gray-600">Pending</div>
        </div>
        <div className="bg-blue-50 rounded-lg p-4 border border-blue-200">
          <div className="text-2xl font-bold text-blue-900">{inProgressRequirements.length}</div>
          <div className="text-sm text-blue-600">In Progress</div>
        </div>
        <div className="bg-green-50 rounded-lg p-4 border border-green-200">
          <div className="text-2xl font-bold text-green-900">{completedRequirements.length}</div>
          <div className="text-sm text-green-600">Completed</div>
        </div>
      </div>

      {/* Requirements List */}
      {safeRequirements.length > 0 ? (
        <div className="space-y-6">
          {/* Pending Requirements */}
          {pendingRequirements.length > 0 && (
            <div>
              <h4 className="text-md font-semibold text-gray-700 mb-3">ðŸ“‹ Pending Requirements</h4>
              <div className="space-y-3">
                {pendingRequirements.map((requirement) => (
                  <RequirementCard
                    key={requirement.id}
                    requirement={requirement}
                    onClick={() => setSelectedRequirement(requirement)}
                  />
                ))}
              </div>
            </div>
          )}

          {/* In Progress Requirements */}
          {inProgressRequirements.length > 0 && (
            <div>
              <h4 className="text-md font-semibold text-gray-700 mb-3">ðŸ”„ In Progress</h4>
              <div className="space-y-3">
                {inProgressRequirements.map((requirement) => (
                  <RequirementCard
                    key={requirement.id}
                    requirement={requirement}
                    onClick={() => setSelectedRequirement(requirement)}
                  />
                ))}
              </div>
            </div>
          )}

          {/* Completed Requirements */}
          {completedRequirements.length > 0 && (
            <div>
              <h4 className="text-md font-semibold text-gray-700 mb-3">âœ… Completed</h4>
              <div className="space-y-3">
                {completedRequirements.map((requirement) => (
                  <RequirementCard
                    key={requirement.id}
                    requirement={requirement}
                    onClick={() => setSelectedRequirement(requirement)}
                  />
                ))}
              </div>
            </div>
          )}
        </div>
      ) : (
        <div className="text-center text-gray-500 py-12 bg-gray-50 rounded-lg">
          <svg className="mx-auto h-12 w-12 text-gray-400 mb-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <p className="text-lg font-medium mb-2">No requirements posted yet</p>
          {isOwner && (
            <button
              onClick={() => setShowCreateModal(true)}
              className="mt-4 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Post First Requirement
            </button>
          )}
        </div>
      )}

      {/* Create Requirement Modal */}
      {showCreateModal && (
        <CreateRequirementModal
          projectId={projectId}
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            onRefresh();
          }}
        />
      )}

      {/* Requirement Details Modal */}
      {selectedRequirement && (
        <RequirementDetailsModal
          requirement={selectedRequirement}
          onClose={() => setSelectedRequirement(null)}
          onRefresh={onRefresh}
          isOwner={isOwner}
        />
      )}
    </div>
  );
};

export default RequirementsTab;
