import React, { useState } from 'react';
import CreateIssueModal from './CreateIssueModal';
import IssueDetailsModal from './IssueDetailsModal';

const IssuesTab = ({ projectId, issues, onRefresh }) => {
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [selectedIssue, setSelectedIssue] = useState(null);

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h3 className="text-lg font-semibold">Project Issues</h3>
        <button
          onClick={() => setShowCreateModal(true)}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          Create Issue
        </button>
      </div>

      <div className="space-y-4">
        {issues.map((issue) => (
          <div
            key={issue.id}
            onClick={() => setSelectedIssue(issue)}
            className="p-4 border border-gray-200 rounded-lg hover:border-blue-500 cursor-pointer"
          >
            <div className="flex justify-between items-start mb-2">
              <h4 className="font-semibold text-gray-900">{issue.title}</h4>
              <span className={`px-2 py-1 text-xs rounded ${
                issue.priority === 'HIGH' ? 'bg-red-100 text-red-800' :
                issue.priority === 'MEDIUM' ? 'bg-yellow-100 text-yellow-800' :
                'bg-green-100 text-green-800'
              }`}>
                {issue.priority}
              </span>
            </div>
            <p className="text-gray-600 text-sm mb-2">{issue.description}</p>
            <div className="flex items-center gap-4 text-xs text-gray-500">
              <span className={`px-2 py-1 rounded ${
                issue.status === 'PENDING' ? 'bg-gray-100' :
                issue.status === 'IN_PROGRESS' ? 'bg-blue-100 text-blue-800' :
                'bg-green-100 text-green-800'
              }`}>
                {issue.status}
              </span>
              {issue.assignee && (
                <span>Assigned to: {issue.assignee.fullName}</span>
              )}
            </div>
          </div>
        ))}
      </div>

      {issues.length === 0 && (
        <div className="text-center text-gray-500 py-12">
          No issues yet. Create one to get started!
        </div>
      )}

      {showCreateModal && (
        <CreateIssueModal
          projectId={projectId}
          onClose={() => setShowCreateModal(false)}
          onSuccess={() => {
            setShowCreateModal(false);
            onRefresh();
          }}
        />
      )}

      {selectedIssue && (
        <IssueDetailsModal
          issue={selectedIssue}
          onClose={() => setSelectedIssue(null)}
          onRefresh={onRefresh}
        />
      )}
    </div>
  );
};

export default IssuesTab;
