import React, { useState, useEffect } from 'react';
import api from '../config/api';

const IssueDetailsModal = ({ issue, onClose, onRefresh }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchComments();
  }, [issue.id]);

  const fetchComments = async () => {
    try {
      const res = await api.get(`/api/comments/${issue.id}`);
      setComments(res.data);
    } catch (err) {
      console.error('Error fetching comments:', err);
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    setLoading(true);
    try {
      await api.post('/api/comments', {
        issueId: issue.id,
        content: newComment
      });
      setNewComment('');
      fetchComments();
    } catch (err) {
      console.error('Error adding comment:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (newStatus) => {
    try {
      await api.put(`/api/issues/${issue.id}/status/${newStatus}`);
      onRefresh();
      onClose();
    } catch (err) {
      console.error('Error updating status:', err);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 max-w-2xl w-full max-h-screen overflow-y-auto">
        <div className="flex justify-between items-start mb-4">
          <h2 className="text-2xl font-bold">{issue.title}</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700 text-2xl">
            âœ•
          </button>
        </div>

        <div className="mb-4">
          <p className="text-gray-600 mb-4">{issue.description}</p>
          <div className="flex gap-4 text-sm">
            <span className={`px-3 py-1 rounded ${
              issue.priority === 'HIGH' ? 'bg-red-100 text-red-800' :
              issue.priority === 'MEDIUM' ? 'bg-yellow-100 text-yellow-800' :
              'bg-green-100 text-green-800'
            }`}>
              {issue.priority}
            </span>
            <select
              value={issue.status}
              onChange={(e) => handleStatusChange(e.target.value)}
              className="px-3 py-1 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="PENDING">Pending</option>
              <option value="IN_PROGRESS">In Progress</option>
              <option value="DONE">Done</option>
            </select>
          </div>
        </div>

        <div className="border-t pt-4">
          <h3 className="font-semibold mb-3">Comments ({comments.length})</h3>
          <div className="space-y-3 mb-4 max-h-64 overflow-y-auto">
            {comments.map((comment) => (
              <div key={comment.id} className="bg-gray-50 p-3 rounded">
                <div className="flex items-center gap-2 mb-1">
                  <span className="font-medium text-sm">{comment.user?.fullName}</span>
                  <span className="text-xs text-gray-500">
                    {new Date(comment.createdDateTime).toLocaleString()}
                  </span>
                </div>
                <p className="text-sm text-gray-700">{comment.content}</p>
              </div>
            ))}
            {comments.length === 0 && (
              <p className="text-gray-500 text-sm">No comments yet</p>
            )}
          </div>

          <form onSubmit={handleAddComment} className="flex gap-2">
            <input
              type="text"
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add a comment..."
              className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
            >
              {loading ? 'Sending...' : 'Comment'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default IssueDetailsModal;
