import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../config/api';
import ProjectCard from '../components/ProjectCard';

const AllProjectsPage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTag, setSelectedTag] = useState('');
  const [searchKeyword, setSearchKeyword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchProjects();
  }, [selectedTag]);

  const fetchProjects = async () => {
    try {
      const params = {};
      if (selectedTag) params.tag = selectedTag;
      const res = await api.get('/api/projects', { params });
      setProjects(res.data);
    } catch (err) {
      console.error('Error fetching projects:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchKeyword.trim()) {
      fetchProjects();
      return;
    }
    try {
      const res = await api.get('/api/projects/search', {
        params: { keyword: searchKeyword }
      });
      setProjects(res.data);
    } catch (err) {
      console.error('Error searching projects:', err);
    }
  };

  const allTags = [...new Set(projects.flatMap(p => p.tags || []))];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-8">All Projects</h1>

      <div className="mb-6 flex gap-4">
        <input
          type="text"
          value={searchKeyword}
          onChange={(e) => setSearchKeyword(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          placeholder="Search projects..."
          className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={handleSearch}
          className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      <div className="mb-6 flex flex-wrap gap-2">
        <button
          onClick={() => setSelectedTag('')}
          className={`px-4 py-2 rounded-full ${!selectedTag ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          All Tags
        </button>
        {allTags.map((tag) => (
          <button
            key={tag}
            onClick={() => setSelectedTag(tag)}
            className={`px-4 py-2 rounded-full ${selectedTag === tag ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
          >
            {tag}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project) => (
          <ProjectCard
            key={project.id}
            project={project}
            onClick={() => navigate(`/project/${project.id}`)}
          />
        ))}
      </div>

      {projects.length === 0 && (
        <div className="text-center text-gray-500 mt-12">
          No projects found
        </div>
      )}
    </div>
  );
};

export default AllProjectsPage;
